import React, {useEffect, useState} from "react";
import axios from "axios";

// Root URL:
const rootUrl = "https://min-api.cryptocompare.com/data";

// *** MOCK DATA ***
import {Data} from "./mockData/allCoins";
const mockUrl = Data;

// Creating Context:
const CoinsContext = React.createContext();

const CoinsProvider = ({children}) => {
    // *** MOCK DATA ***
    const [allCoins, setAllCoins] = useState("");
    useEffect(()=>{
     const fetchAllCoins = () => {
           axios.get(mockUrl)  
           .then(response => {
               const myCoins = response.Data;
               setAllCoins(myCoins);
           })         

        };
    },[])
    return <CoinsContext.Provider value={"Hello"}>
        {children}
    </CoinsContext.Provider>
}

export {CoinsProvider, CoinsContext};
